using System;using System.Threading;using System.Threading.Tasks;using DotNetCore.CAP.Monitoring;using DotNetCore.CAP.Persistence;using Microsoft.Extensions.Diagnostics.HealthChecks;namespace DotNetCore.CAP.HealthCheck.MongoDB.Received;public class MongoDBReceivedTableHealthCheck : IHealthCheck{    private readonly IMonitoringApi _monitoringApi;    public MongoDBReceivedTableHealthCheck(IDataStorage dataStorage)    {        _monitoringApi = dataStorage.GetMonitoringApi();    }    public Task<HealthCheckResult> CheckHealthAsync(HealthCheckContext context,        CancellationToken cancellationToken = new CancellationToken())    {        HealthCheckResult result;        int failedCount = _monitoringApi.ReceivedFailedCount();        try        {            result = failedCount > 0                ? HealthCheckResult.Unhealthy($"Failed Count: {failedCount}")                : HealthCheckResult.Healthy();        }        catch (Exception e)        {            result = HealthCheckResult.Unhealthy(e.Message, e);        }        return Task.FromResult(result);    }}